mapper.select:
  select bbsno, btitle, nick, to_char(bdate,'yyyy-mm-dd HH24:MI:SS') bdate, bview, bcontents, r, bcname
    from (
    select bbsno, btitle, nick, bdate, bview, bcontents,  rownum r, bcname
    from (
    select bbsno, btitle, member.nick nick, bdate, bview, bcontents, bcategory.bcatename as bcname

    from bbs 
    inner join bcategory
     on bbs.bcate = bcategory.bcate

     inner join member
    on bbs.id = member.id


        ORDER BY bbsno DESC

		)
		)

       where r >= #{sno} and r <= #{eno}

 select bbsno, btitle, nick, to_char(bdate,'yyyy-mm-dd HH24:MI:SS') bdate, bview, bcontents, bcate, r
from (
select bbsno, btitle, nick, bdate, bview, bcontents, bcate, rownum r
from (
select bbsno, btitle, member.nick nick, bdate, bview, bcontents, bcate

from bbs inner join member
on bbs.id = member.id
WHERE bbsno=#{bbsno}
))

  select bbsno, btitle, nick, to_char(bdate,'yyyy-mm-dd HH24:MI:SS') bdate, bview, bcontents, r, bcname
    from (
    select bbsno, btitle, nick, bdate, bview, bcontents,  rownum r, bcname
    from (
    select bbsno, btitle, member.nick nick, bdate, bview, bcontents, bcategory.bcatename as bcname

    from bbs 
    inner join bcategory
     on bbs.bcate = bcategory.bcate

     inner join member
    on bbs.id = member.id

   where bview >= 10

       ORDER BY bbsno DESC

		)
		)

       where r >= #{sno} and r <= #{eno}
