mapper.select:
select reno, recontents, redate,
		bbsno, id, nick, r
from (
select reno, recontents, redate,
		bbsno, id, nick, rownum r
from (
select reno, recontents, redate,
		bbsno, r.id id, m.nick nick
from reply r inner join member m
on r.id = m.id
where bbsno = #{bbsno}
order by reno desc
)
)

where r >= #{sno} and r <= #{eno}

mapper.update:
update reply
set recontents = #{recontents}
where reno = #{reno}
