mapper.update:
update ticket
set filename = #{filename}
where ticketno = #{ticketno}

update ticket
set stadium =#{stadium},
tickettitle = #{tickettitle},
ticketcontents = #{ticketcontents}
where ticketno =#{ticketno}

mapper.select:
       select ticketno, tickettitle, nick, to_char(ticketdate,'yyyy-mm-dd HH24:MI:SS') ticketdate, stadium, ticketcontents, filename ,id ,mfilename ,r
		from (
			select ticketno, tickettitle, nick, ticketdate, stadium, ticketcontents, filename, id, mfilename, rownum r
			from(
				select ticketno, tickettitle, m.nick nick, ticketdate, stadium, ticketcontents, t.filename filename, t.id id  ,m.filename mfilename
				from ticket t  inner join member m
				on t.id =m.id )
				)
        where ticketno = #{ticketno}

		select ticketno, tickettitle, nick, to_char(ticketdate,'yyyy-mm-dd HH24:MI:SS') ticketdate, stadium, ticketcontents, filename ,id ,r
		from (
			select ticketno, tickettitle, nick, ticketdate, stadium, ticketcontents, filename,id, rownum r
			from(
				select ticketno, tickettitle, m.nick nick, ticketdate, stadium, ticketcontents, t.filename filename, t.id id
				from ticket t  inner join member m
				on t.id =m.id 



				 	m.nick like '%'||#{word}||'%'



					stadium like '%'||#{word}||'%'



		       order by ticketno desc
			)

         )where r >= #{sno} and r <= #{eno}
